import React from 'react';

function conexaoBanco(){

    module.exports = {
        // Outras configurações do webpack...
      
        resolve: {
          fallback: {
            "buffer": require.resolve("buffer/"),
          }
        }
      };

    const mysql = require('mysql');

    const connection = mysql.createConnection({
    host: 'localhost', // O host padrão do XAMPP é localhost
    user: 'root',      // O usuário padrão do XAMPP é root
    password: '',      // A senha padrão do XAMPP é vazia por padrão
    database: 'quadrarts' // Substitua 'seu_banco_de_dados' pelo nome do seu banco de dados
    });

    // Conectar ao banco de dados
    connection.connect((err) => {
    if (err) {
        console.error('Erro ao conectar ao banco de dados:', err);
        return;
    }
    console.log('Conexão bem-sucedida com o banco de dados MySQL');
    });

    // Exportar a conexão para que você possa usá-la em outros lugares do seu aplicativo
    module.exports = connection;
}

export default conexaoBanco;
